calc.equation_logic_text:
var calc_output = [];

var bmi = parseFloat(bmi);
var circ = parseFloat(circ);
var trig = parseFloat(trig);
var ggt = parseFloat(ggt);

if (UOMSYSTEM == true) {
	circ = circ / 0.39370079;
    trig = trig;
}
else {
	circ = circ;
  	trig = trig*88.57;
}

var y = 0.953 * Math.log(trig) + 0.139 * bmi + 0.718 * Math.log(ggt) + 0.053 * circ - 15.745;
var fli = Math.exp(y) / (1 + Math.exp(y)) * 100;

var risk, msg;

if (fli.toFixed(0) < 30) {
	risk = 'Low';
	msg = 'Fatty liver ruled out (LR- = 0.2)';
}
else if (fli.toFixed(0) >= 30 && fli.toFixed(0) < 60) {
	risk = 'Indeterminate';
	msg = 'Fatty liver neither ruled in nor ruled out';
}
else if (fli.toFixed(0) >= 60) {
	risk = 'High';
	msg = 'Fatty liver ruled in (LR+ = 4.3)';
}

calc_output.push({
   name: 'mini',
   value: fli.toFixed(0),
   value_text: '',
   message: risk + ' risk'
});
calc_output.push({
   name: 'FLI',
   value: fli.toFixed(0),
   value_text: '',
   message: 'Fatty Liver Index'
});
calc_output.push({
   name: 'FLI',
   value: risk,
   value_text: 'risk',
   message: msg
});
