calc.equation_logic_text:
var calc_output = [];

var age = parseFloat(age)
var iop = parseFloat(iop)
var cct = parseFloat(cct)
var cdr = parseFloat(cdr)
var stdev = parseFloat(stdev)

var score = age + iop + cct + cdr + stdev

var risk
if (score <= 6) {
	risk = '≤4.0'
}
else if (score == 7 || score == 8) {
	risk = '10'
}
else if (score == 9 || score == 10) {
	risk = '15'
}
else if (score == 11 || score == 12) {
	risk = '20'
}
else {
	risk = '≥33'
}

var group, msg
if (score <= 6) {
	group = 'Low'
	msg = 'Recommend observation every 6 months'
}
else if (score >= 7 && score <= 12) {
	group = 'Intermediate'
	msg = 'Recommend counseling on risks/benefits of treatment versus close observation'
}
else {
	group = 'High'
	msg = 'Recommend initiating treatment'
}

//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: msg
});
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: 'OHTS Score'
});
calc_output.push({
    name: 'group',
    value: group,
    value_text: 'risk',
    message: msg
});
calc_output.push({
    name: 'risk',
    value: risk,
    value_text: '%',
    message: '5-year risk of developing primary open angle glaucoma'
});
