var rr = parseFloat(rr)
var hr = parseFloat(hr)
var bp = parseFloat(bp)
var age = parseFloat(age)

var score = rr + hr + bp + age 

var risk

if (score <= 20) {
	risk = 'Low'
}
else if (score > 20) { 
	risk = 'High'
}

//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: risk + ' risk'
});
