var calc_output = [];

var sensory = parseFloat(sensory)
var moisture = parseFloat(moisture)
var activity = parseFloat(activity)
var mobility = parseFloat(mobility)
var nutrition = parseFloat(nutrition)
var friction = parseFloat(friction)

var score = sensory + moisture + activity + mobility + nutrition + friction
var risk


if (score <= 12) {
    risk = 'High'
}
else if (score >= 13 && score <= 15) {
    risk = 'Moderate'
}
else if (score >= 15 && score <= 17) {
    risk = 'Mild'
}
else {
    risk = 'Average'
}

//results 
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: risk + ' risk'
});
calc_output.push({
    name: 'score',
    value: score,
    value_text: 'points',
    message: 'Braden Score'
});
calc_output.push({
    name: 'risk',
    value: risk, 
    value_text: ' risk',
    message:''
});
