var calc_output = [];

var disease = parseFloat(disease)
var cytogenetics = parseFloat(cytogenetics)
var hla = parseFloat(hla)
var blasts = parseFloat(blasts)
var karnofsky = parseFloat(karnofsky)

var score = disease + cytogenetics + hla + blasts + karnofsky

var survival 


if (score == 0) {
    survival = '42' 
}    
else if (score == 1) { 
    survival = '28'
}
else if (score == 2){
    survival = '15'
} 
else if (score >= 3) {
    survival = '6'
}

//results 
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: survival + '% overall survival' 
    });
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: 'Duval Score'
    });
calc_output.push({
    name: 'survival',
    value: survival, 
    value_text: '%',
    message: '3-year overall survival after transplantation'
});
