calc.equation_logic_text:
var calc_output = [];

var age = parseFloat(age);
var sex = parseFloat(sex);
var previosgiadmit = parseFloat(previosgiadmit);
var dre = parseFloat(dre);
var hr = parseFloat(hr);
var sbp = parseFloat(sbp);
var hb = parseFloat(hb);


var score = age + sex + previosgiadmit + dre + hr + sbp + hb;

var discharge, rec, mini_rec;

if (score <= 8) {
  rec = 'Consider discharge, with appropriate precautions.';
  mini_rec = 'Consider discharge';
}
else {
  rec = 'Discharge NOT recommended. Consider admission with further workup and resuscitation as necessary.';
	mini_rec = 'Discharge NOT recommended';
}

if (score <= 2) {
    discharge = '99';
}    
else if (score == 3 | score == 4) { 
    discharge = '98'; 
}
else if (score == 5) { 
    discharge = '97'; 
} 
else if (score == 6 | score == 7) { 
    discharge = '96'; 
} 
else if (score == 8) { 
    discharge = '95'; 
} 
else if (score == 9) { 
    discharge = '93'; 
} 
else if (score == 10) { 
    discharge = '91'; 
} 
else if (score == 11) { 
    discharge = '89'; 
} 
else if (score == 12 | score == 13) { 
    discharge = '87-89'; 
} 
else if (score == 14 | score == 15) { 
    discharge = '77-81'; 
} 
else if (score == 16 | score == 17) { 
    discharge = '67-72'; 
} 
else if (score >= 18 && score <= 20) { 
    discharge = '50-62'; 
} 
else if (score >= 21 && score <= 23) { 
    discharge = '33-45'; 
} 
else if (score >= 24 && score <= 26) { 
    discharge = '20-28'; 
} 
else if (score >= 27 && score <= 29) { 
    discharge = '11-16'; 
} 
else if (score >= 30) { 
    discharge = '0-1'; 
} 
//results 
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: mini_rec
    });
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: 'Oakland Score (<guideline id ="502">ACG guideline</guideline>)'
    });
calc_output.push({
    name: 'discharge',
    value: discharge, 
    value_text: '%',
    message: 'Probability of safe discharge (absence of rebleeding, blood transfusion, therapeutic intervention, 28 day readmission, or death). ' + rec
});
