calc.equation_logic_text:
var calc_output = [];

var depressedmood = parseFloat(depressedmood);
var guilt = parseFloat(guilt);
var suicide = parseFloat(suicide);
var initialinsomnia = parseFloat(initialinsomnia);
var insomnia = parseFloat(insomnia);
var delayed = parseFloat(delayed);
var workinterests = parseFloat(workinterests);
var retardation = parseFloat(retardation);
var agitation = parseFloat(agitation)
var panxiety = parseFloat(panxiety)
var sanxiety = parseFloat(sanxiety)
var gastrosom = parseFloat(gastrosom)
var somaticsymptoms = parseFloat(somaticsymptoms);
var genitalsymptoms = parseFloat(genitalsymptoms);
var hypochondriasis = parseFloat(hypochondriasis);
var weightloss = parseFloat(weightloss);
var insight = parseFloat(insight);


var score = depressedmood + guilt + suicide + initialinsomnia + insomnia + delayed + workinterests + retardation + agitation + panxiety + sanxiety + gastrosom + somaticsymptoms + genitalsymptoms + hypochondriasis + weightloss + insight

var grade = ''


if (score >= 0 && score <= 7) {
    grade = 'Normal'
} else if (score >= 8 && score <= 13) {
    grade = 'Mild'
} else if (score >= 14 && score <= 18) {
    grade = 'Moderate'
} else if (score >= 19 && score <= 22) {
    grade = 'Severe'
} else if (score >= 23) {
    grade = 'Very severe'
}


//results 

if (score <= 7) {
    calc_output.push({
        name: 'mini',
        value: 'No depression',
        value_text: '',
        message: score + ' points'
    });
    calc_output.push({
        name: 'score',
        value: score,
        value_text: 'points',
        message: 'HAM-D Score'
    });
    calc_output.push({
        name: 'grade',
        value: 'Normal',
        value_text: '',
        message: 'No depression'
    });
} else if (score > 7) {
    calc_output.push({
        name: 'mini',
        value: grade,
        value_text: ' depression',
        message: score + ' points'
    });
    calc_output.push({
        name: 'score',
        value: score,
        value_text: 'points',
        message: 'HAM-D Score'
    });
    calc_output.push({
        name: 'grade',
        value: grade,
        value_text: 'depression',
        message: ''
    });
}
