calc.equation_logic_text:
var calc_output = [];

spleen = parseFloat(spleen)
liver = parseFloat(liver)
lymph = parseFloat(lymph)
anemia = parseFloat(anemia)
thrombocytopenia = parseFloat(thrombocytopenia)

var rai
var risk
var survival

if (thrombocytopenia == 1) {
	rai = 'IV'
  	risk = 'High'
  	survival = '<4'
	}
else if (anemia == 1) {
	rai = 'III'
  	risk = 'High'
  	survival = '<4'
	}
else if (spleen == 1| liver == 1) {
	rai = 'II'
  	risk = 'Intermediate'
  	survival = '7'
	}
else if (lymph == 1) {
	rai = 'I'
  	risk = 'Intermediate'
  	survival = '7'
	}
else {
	rai = '0'
	risk = 'Low'
	survival = '>10' 
	}

//results
calc_output.push({
    name: 'mini',
    value: 'Stage ' + rai,
    value_text: '',
    message: survival + ' years OS'
});
calc_output.push({
    name: 'classification',
    value: 'Stage ' + rai,
    value_text: '',
    message: 'Rai Stage'
});
calc_output.push({
    name: 'Risk',
    value: risk,
    value_text: 'risk',
    message: ''
});
calc_output.push({
    name: 'survival',
    value: survival,
    value_text: 'years',
    message: 'Overall survival'
});
