calc.equation_logic_text:
var calc_output = [];

var age = parseFloat(age)
var wbc = parseFloat(wbc)
var hb = parseFloat(hb)
var blood = parseFloat(blood)
var symptoms = parseFloat(symptoms)

var score = age + wbc + hb + blood + symptoms

var risk
var median 


if (score == 0) {
    risk = 'Low' 
    median = '>14.2'
}    
else if (score >= 1 && score <= 2){ 
    risk = 'Intermediate-1'
    median = '14.2'
}
else if (score >= 3 && score <= 4){
    risk = 'Intermediate-2'
    median = '4'
} 
else if (score >= 5 && score <= 6){
    risk = 'High'
    median = '1.5'
}

//results 
if (risk == 'Low') {
calc_output.push({
    name: 'mini',
    value: risk,
    value_text: 'risk',
    message: 'Median survival not reached'  
    });
}
else {
  calc_output.push({
    name: 'mini',
    value: risk,
    value_text: 'risk',
    message: median + ' years median survival'  
    });
}
calc_output.push({
    name: 'score',
    value: score,
    value_text: 'points',
    message: 'DIPSS Score'
    });
calc_output.push({
    name: 'risk',
    value: risk, 
    value_text: 'risk',
    message: 'Risk group'
});

if (risk == 'Low') {
calc_output.push({
    name: 'median',
    value: '', 
    value_text: '',
    message: 'Median survival not reached in original studies (see Evidence for more data)'
});
}
else {
  calc_output.push({
    name: 'median',
    value: median, 
    value_text: 'years',
    message: 'Median survival'
});
}
