var calc_output = [];

var stenosis = parseFloat(stenosis)
var inflam = parseFloat(inflam)
var injury = parseFloat(injury)

var score = stenosis + inflam + injury

var diagnosis 

if (score == 0) {
  diagnosis = 'No activity by NAFLD Activity Score'
}

else if (score == 1 || score == 2) {
    diagnosis = 'Mild activity' 
}    
else if (score >= 3 && score <= 5) { 
    diagnosis = 'Moderate activity'
}
else {
    diagnosis = 'Marked activity'
}

//results 
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: diagnosis 
    });
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: 'NAFLD Activity Score'
    });
calc_output.push({
    name: 'diagnosis',
    value: '', 
    value_text: '',
    message: diagnosis
});
