var calc_output = [];

var age = parseFloat(age)
var ef = parseFloat(ef)
var creat = parseFloat(creat)
var emsurg = parseFloat(emsurg)
var hematocrit = parseFloat(hematocrit)

var hemvalue

if (hematocrit < 36) {
  hemvalue = 0.2 * (36 - hematocrit)
}
else if (hematocrit >=36) {
  hemvalue = 0
}

var acef = ((age / ef) + creat + emsurg + hemvalue)

var x = -4.86 + (0.75 * acef)

var mort = (Math.exp(x) / (1 + Math.exp(x))) * 100

//results
calc_output.push({
    name: 'mini',
    value: acef.toFixed(1),
    value_text: '',
    message: 'ACEF II Risk Score' 
    });
calc_output.push({
    name: 'ACEF',
    value: acef.toFixed(1),
    value_text: 'points',
    message: 'ACEF II Risk Score'
    });
calc_output.push({
    name: 'mort',
    value: mort.toFixed(1),
    value_text: '%',
    message: 'Predicted 30-day operative mortality'
    });
