calc.equation_logic_text:
var calc_output = [];

var bmi = parseFloat(bmi);
var ast = parseFloat(ast);
var alt = parseFloat(alt);
var diabetes = parseFloat(diabetes);

var ratio = ast/alt;

var x;

if (ratio >= 0.8) {
    x = 2;
}
else if (ratio < 0.8) {
    x = 0;
}

var score = bmi + x + diabetes; 

var risk;

if (score <= 1) {
    risk = 'Low';
}
else {
    risk = 'High';
}

//results 
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: risk +' risk'
    });
calc_output.push({
    name: 'score',
    value: score,
    value_text: 'points',
    message: 'BARD Score'
    });
if (score <= 1) {
calc_output.push({
    name: 'risk',
    value: risk,
    value_text: 'risk',
    message: ''
    });
}
else { 
calc_output.push({
    name: 'risk',
    value: risk,
    value_text: 'risk',
    message: 'Scores 2-4 were associated with odds ratio 17 (see Evidence for details). In low risk patients (scores 0-1), consider avoiding biopsy.'
    });
}
