var age = parseFloat(age);
var afp13 = parseFloat(afpl3);
var afp = parseFloat(afp);
var dcp = parseFloat(dcp);
var gender = parseFloat(gender);

var score = -10.08 + 0.09*age + 1.67*gender + 2.34*Math.log10(afp) + 0.04*afpl3 + 1.33*Math.log10(dcp); 

var prob = (Math.exp(score) / (1 + Math.exp(score)))*100;

//results

calc_output.push({
   name: 'mini',
   value: prob.toFixed(2),
   value_text: '%',
   message:'Probability of concurrent HCC'
});
