calc.equation_logic_text:
var calc_output = [];

var pos = parseFloat(pos);
var neuro = parseFloat(neuro);
var morph = parseFloat(morph);

var score = pos + neuro + morph;

var rec, msg;

if (score >= 0 && score <= 3) {
  rec = 'Non-operative';
  msg = 'Recommend bracing';
} else if (score == 4) {
  rec = 'Non-operative or operative';
  msg = '';
} else {
  rec = 'Operative';
  msg = 'Stabilization with or without fracture reduction as appropriate';
}

//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: rec
});
calc_output.push({
    name: 'score',
    value: score, 
    value_text: 'points',
    message: 'TLICS'
});
calc_output.push({
    name: 'risk1',
    value: rec, 
    value_text: '',
    message: msg
});
