calc.equation_logic_text:
//vars
var calc_output = [];
var myo = parseFloat(myo);
var vt = parseFloat(vt);
var post = parseFloat(post);
var heart = parseFloat(heart);
var age = parseFloat(age);
var weight = parseFloat(weight);
var liver = parseFloat(liver);
var cns = parseFloat(cns);
var renal = parseFloat(renal);
var chronic = parseFloat(chronic);
var duration = parseFloat(duration);
var peak = parseFloat(peak);
var arrest = parseFloat(arrest);
var dbp = parseFloat(dbp);
var pulse = parseFloat(pulse);
var hco = parseFloat(hco);

//score
var score = myo + vt + post + heart + age + weight + liver + cns + renal + chronic + duration + peak + arrest + dbp + pulse + hco;

score = score - 6;

//risk
var risk, surv;
if (score > 5) {
  risk = 'I';
  surv = '75';
} else if (score >= 1 && score <= 5) {
  risk = 'II';
  surv = '58';
} else if (score >= -4 && score <= 0) {
  risk = 'III';
  surv = '42';
} else if (score >= -9 && score <= -5) {
  risk = 'IV';
  surv = '30';
} else if (score <= -10) {
  risk = 'V';
  surv = '18';
}

//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: surv + '% survival' 
});
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: 'SAVE Score'
});
calc_output.push({
    name: 'Score',
    value: 'Class ' + risk,
    value_text: '',
    message: 'Risk class' 
});
calc_output.push({
    name: 'Score',
    value: surv,
    value_text: '%',
    message: 'In-hospital survival' 
});
