calc.equation_logic_text:
var calc_output = [];
var renal = parseFloat(renal);
var neuro = parseFloat(neuro);
var age = parseFloat(age);
var diab = parseFloat(diab);
var sex = parseFloat(sex);
var pci = parseFloat(pci);
var bp = parseFloat(bp);

age = age - 18;
age = age/10;
age = Math.floor(age);

var score = renal + neuro + age + diab + sex + pci + bp;

score = score.toFixed(0);

var rec, risk;

if (score <= 13) {
  risk = '2';
  rec = 'Extremely low';
} else if (score >= 14 && score <= 15) {
  risk = '8';
  rec = 'Low';
} else if (score >= 16 && score <= 17) {
  risk = '9';
  rec = 'Moderate';
} else if (score >= 18 && score <= 19) {
  risk = '17';
  rec = 'High';
} else if (score >= 20) {
  risk = '34';
  rec = 'Extremely high';
}

//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: risk + '% 30-day readmission risk'
});
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: 'READMITS Score'   
    });
calc_output.push({
    name: 'risk1',
    value: rec, 
    value_text: 'risk',
    message: ''
});
calc_output.push({
    name: 'risk1',
    value: risk, 
    value_text: '%',
    message: '30-day readmission risk'
});
