calc.equation_logic_text:
var calc_output = [];

var a = parseFloat(a);
var b = parseFloat(b);
var c = parseFloat(c);
var d = parseFloat(d);
var e = parseFloat(e);
var f = parseFloat(f);
var g = parseFloat(g);
var h = parseFloat(h);
var i = parseFloat(i);
var j = parseFloat(j);
var episodes = parseFloat(episodes);

var s1 = a + b + h + i + j;
var s2 = c + d + e + f + g;

var diagnosis;

if (s1 == 5 && s2 >= 3) {
    diagnosis = 'Positive';
} else {
    diagnosis = 'Not positive';
}

var severity;

if (episodes == 1) {
    severity = 'Mild';
} else if (episodes == 2) {
    severity = 'Moderate';
} else if (episodes == 3) {
    severity = 'Severe';
} else if (episodes == 4) {
    severity = 'Extreme';
}

calc_output.push({
    name: 'mini',
    value: diagnosis,
    value_text: '',
    message: diagnosis + ' for BED'
});
calc_output.push({
    name: 'Diagnosis',
    value: diagnosis,
    value_text: '',
    message: diagnosis + ' for BED by DSM-5 Criteria'
});

if (episodes > 0) {
    calc_output.push({
        name: 'Severity',
        value: severity,
        value_text: '',
        message: 'Level of severity'
    });
}
