var cdr;
var secondary = [o, jps, ca, hh, pc];
var count = {};

secondary.forEach(function(arg) {
    if (count[arg] != undefined) { 
        count[arg]++; 
    } else {
      count[arg] = 1;
    }
});

const left = [];
const right = [];

secondary.forEach(arg => {
  if (arg < m) {
    left.push(arg);
  } else if (arg > m) {
    right.push(arg);
  }
});

function modeArray(array)
{
    if (array.length == 0)
        return null;
    var modeMap = {},
        maxCount = 1, 
        modes = [];

    for(var i = 0; i < array.length; i++)
    {
        var el = array[i];

        if (modeMap[el] == null)
            modeMap[el] = 1;
        else
            modeMap[el]++;

        if (modeMap[el] > maxCount)
        {
            modes = [el];
            maxCount = modeMap[el];
        }
        else if (modeMap[el] == maxCount)
        {
            modes.push(el);
            maxCount = modeMap[el];
        }
    }
    return modes;
}

