  // console.log('hitting the right spot!')
  const upper = secondary.filter(arg => arg > m);
  const lower = secondary.filter(arg => arg < m);

  if (upper.length > 2) {
  	var mode = modeArray(upper);
	cdr = mode.sort()[0];
  } else if (lower.length > 2) {
