var calc_output = [];
var time = parseFloat(time);
var tone = parseFloat(tone);
var touch = parseFloat(touch);
var yawn = parseFloat(yawn);
var move = parseFloat(move);
var sweat = parseFloat(sweat);
var tremor = parseFloat(tremor);
var sbs = parseFloat(sbs);
var temp = parseFloat(temp);
var vomit = parseFloat(vomit);
var stools = parseFloat(stools);

var score = time + tone + touch + yawn + move + sweat + tremor + sbs + temp + vomit + stools;

//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: 'WAT-1 Score'
});
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: 'WAT-1 Score'
});
