calc.equation_logic_text:
var calc_output = [];

var ideation = parseFloat(ideation);
var behavior = parseFloat(behavior);
var behavior_when = parseFloat(behavior_when);

var risk, val, msg;
if (ideation == 1 || ideation == 2) {
  risk = 'Low';
  val = 'risk';
  msg = 'Behavioral health referral at discharge';
} else if (ideation == 3) {
  risk = 'Moderate';
  val = 'risk';
  msg = 'Behavioral health consult (psychiatric nurse/social worker) and consider patient safety precautions';
} else if (ideation == 4 || ideation == 5) {
  risk = 'High';
  val = 'risk';
  msg = 'Immediate notification of physician and/or behavioral health and patient safety precautions';
} else {
  risk = "No ideation";
  val = '';
}

if (ideation <= 2 && behavior > 0 && behavior_when == 0) {
  risk = 'Moderate';
  val = 'risk';
  msg = 'Behavioral health consult (psychiatric nurse/social worker) and consider patient safety precautions';
} else if (ideation <= 3 && behavior > 0 && behavior_when == 1) {
  risk = 'High';
  val = 'risk';
  msg = 'Immediate notification of physician and/or behavioral health and patient safety precautions';
}

//results
calc_output.push({
    name: 'mini',
    value: risk,
    value_text: val,
    message: 'Risk of suicide'
});
calc_output.push({
    name: 'Score',
    value: risk,
    value_text: val,
    message: msg 
});
