calc.equation_logic_text:
var calc_output = [];
var bun = parseFloat(bun);
var ascites = parseFloat(ascites);
var hemo = parseFloat(hemo);
var platelet = parseFloat(platelet);
var ast = parseFloat(ast);
var inr = parseFloat(inr);

if (UOMSYSTEM == false) {
  hemo = hemo * 0.1;
  bun = bun * 2.8;
}

var A = (8.5*inr) + (ast / 35);
var B = (platelet / 150) + (bun / 20) + (hemo / 15);
var score = (A / B) + ascites;
var risk1, rec;

score = score.toFixed(2);

if (score <= 3.90) {
  risk1 = '<5';
  rec = 'expectant management';
} else {
  risk1 = '≥5';
  rec = 'endoscopy';
}

//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: risk1 + '% probability of finding varices'
});
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: 'EVendo Score'   
    });
calc_output.push({
    name: 'risk1',
    value: risk1, 
    value_text: '%',
    message: 'Probability of finding any esophageal varices or varices needing treatment<br/><br/>Recommendation: ' + rec
});
