calc.equation_logic_text:
var calc_output = [];
var aorta = parseFloat(aorta);
var weight = parseFloat(weight);
var sbp = parseFloat(sbp);
var mi = parseFloat(mi);
var urgency = parseFloat(urgency);
var lv = parseFloat(lv);
var one = parseFloat(one);
var endo = parseFloat(endo);
var renal = parseFloat(renal);
var previous = parseFloat(previous);
var neuro = parseFloat(neuro);
var pulmonary = parseFloat(pulmonary);
var eca = parseFloat(eca);
var sex = parseFloat(sex);
var age = parseFloat(age);
var diabetes = parseFloat(diabetes);
var ccs = parseFloat(ccs);
var nyha = parseFloat(nyha);

var x_age;
if (age <= 60) {
  x_age = 1;
} else {
  x_age = (age - 60) + 1;
}

age = 0.0285181;

var y = -5.324537 + age*x_age + aorta +weight +sbp +mi +urgency +lv +one+endo +renal +previous +neuro +pulmonary +eca +sex +diabetes +ccs +nyha;

var prob = Math.exp(y) / (1 + Math.exp(y));

prob = prob*100;
prob = prob.toFixed(2);

//results
calc_output.push({
    name: 'mini',
    value: prob,
    value_text: '%',
    message: 'Predicted mortality by EuroSCORE II'
});
calc_output.push({
    name: 'Score',
    value: prob,
    value_text: '%',
    message: 'Predicted mortality by EuroSCORE II'   
});
