calc.equation_logic_text:
var calc_output = [];
var lower_percent = parseFloat(lower_percent);
var lower_ery = parseFloat(lower_ery);
var lower_scale = parseFloat(lower_scale);
var lower_indu = parseFloat(lower_indu);

var head_percent = parseFloat(head_percent);
var head_ery = parseFloat(head_ery);
var head_scale = parseFloat(head_scale);
var head_indu = parseFloat(head_indu);

var upper_percent = parseFloat(upper_percent);
var upper_ery = parseFloat(upper_ery);
var upper_scale = parseFloat(upper_scale);
var upper_indu = parseFloat(upper_indu);

var trunk_percent = parseFloat(trunk_percent);
var trunk_ery = parseFloat(trunk_ery);
var trunk_scale = parseFloat(trunk_scale);
var trunk_indu = parseFloat(trunk_indu);

var head = head_ery + head_scale + head_indu;
head = head*head_percent*0.1;

var upper = upper_ery + upper_scale + upper_indu;
upper = upper*upper_percent*0.2;

var trunk = trunk_ery + trunk_scale + trunk_indu;
trunk = trunk*trunk_percent*0.3;

var lower = lower_ery + lower_scale + lower_indu;
lower = lower*lower_percent*0.4;

var score = head + upper + trunk + lower;

//results
calc_output.push({
    name: 'mini',
    value: score.toFixed(1),
    value_text: 'points',
    message: 'Higher scores = higher severity'
});
calc_output.push({
    name: 'Score',
    value: score.toFixed(1),
    value_text: 'points',
    message: 'Higher PASI scores indicate higher severity of psoriasis; scores range from 0 (no disease) to 72 (maximal disease severity)'   
});
calc_output.push({
    name: 'Score',
    value: '',
    value_text: '',
    message: 'Head/neck: ' + head.toFixed(1) + '<br/>Upper limbs: ' + upper.toFixed(1) + '<br/>Trunk: ' + trunk.toFixed(1)+ '<br/>Lower limbs: ' + lower.toFixed(1)   
});
