calc.equation_logic_text:
var calc_output = [];
var cabg = parseFloat(cabg);
var peri = parseFloat(peri);
var intu = parseFloat(intu);
var hr = parseFloat(hr);
var walk = parseFloat(walk);
var ekg = parseFloat(ekg);
var xray = parseFloat(xray);
var hemo = parseFloat(hemo);
var urea = parseFloat(urea);
var co2 = parseFloat(co2);

var score = cabg +
peri +
intu +
hr +
walk +
ekg +
xray +
hemo +
urea +
co2;

risk = ['2.2',
'4.0',
'7.2',
'12.5',
'20.9',
'32.9',
'47.5',
'62.6',
'75.6',
'0',
'91.4']

var risk1, risk2;

if (score <= 10) {
	risk1 = risk[score];
} else {
	risk1 = '';
}

if (score == 0) {
	risk2 = 'Low';
} else if (score == 1 || score == 2) {
	risk2 = 'Medium';
} else if (score == 3 || score == 4) {
	risk2 = 'High';
} else {
	risk2 = 'Very high';
}

//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: risk2 + ' risk'
});
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: 'COPD Risk Scale'   
});
calc_output.push({
    name: 'Score',
    value: risk2,
    value_text: 'risk',
    message: 'Risk group'   
});
if (score != 9 && score <= 10)
calc_output.push({
    name: 'Score',
    value: risk1,
    value_text: '%',
    message: 'Risk of adverse event (MI, intubation, etc; see Evidence for details)'   
});
