var calc_output = [];

var loss = parseFloat(loss);
var diameter = parseFloat(diameter);
var path = parseFloat(path);
var gland = parseFloat(gland);

var score = loss + diameter + path + gland;

var risk, prevalence;

if (score == 0) {
  risk = 'Negligible';
  prevalence = '~0';
} else if (score >=1 && score <=2) {
  risk = 'Low';
  prevalence = '6.6';
} else if (score >=3 && score <=6) {
  risk = 'Intermediate';
  prevalence = '12.9';
} else if (score >=7 && score <=10) {
  risk = 'High';
  prevalence = '28.1';
} 

//results
calc_output.push({
    name: 'mini',
    value: prevalence,
    value_text: '%',
    message: risk + ' risk'
});
