var calc_output = [];

var age = parseFloat(age);
var time = parseFloat(time);
var year = parseFloat(year);
var homeaddress = parseFloat(homeaddress);
var jobs = parseFloat(jobs);
var date = parseFloat(date);
var ww1 = parseFloat(ww1);
var president = parseFloat(president);
var count = parseFloat(count);
var recall = parseFloat(recall);

var score = age + time + year + homeaddress + jobs + date + ww1 + president + count + recall;

var diag;

if (score >= 0 && score <= 3) {
  diag = 'Severe cognitive impairment';
} else if (score >= 4 && score <= 6) {
  diag = 'Moderate cognitive impairment';
} else {
  diag = 'No delirium';
}

//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: diag
});
