calc.equation_logic_text:
var calc_output = [];

var age = parseFloat(age);
var hemo1 = parseFloat(hemo1) || 0;
var hemo2 = parseFloat(hemo2) || 0;
var sex = parseFloat(sex);
var history = parseFloat(history);
var gfr = parseFloat(gfr);
var treatment = parseFloat(treatment);

if (sex == 0) {
  var score = age + hemo1 + history + gfr + treatment;
} else {
  var score = age + hemo2 + history + gfr + treatment;
}

var risk, bleeds;

if (score >= 0 && score <= 2) {
  risk = 'Low';
  bleeds = '2.4';
} else if (score == 3) {
  risk = 'Medium';
  bleeds = '4.7';
} else {
  risk = 'High';
  bleeds = '8.1';
}

//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: risk + ' risk'
});
calc_output.push({
    name: '10227_ORBIT Score_result (score)',
    value: score,
    value_text: 'points',
    message: 'ORBIT Score'   
});
calc_output.push({
    name: '10227_ORBIT Score_interpretation',
    value: risk, 
    value_text: 'risk',
    message: 'Risk group'
});
calc_output.push({
    name: '10227_ORBIT Score_result (bleeds)',
    value: bleeds, 
    value_text: 'bleeds',
    message: 'Per 100 patient-years'
});
