calc.equation_logic_text:
var calc_output = [];

var bowel = parseFloat(bowel);
var blood = parseFloat(blood);
var pyr = parseFloat(pyr);
var hr = parseFloat(hr);
var an = parseFloat(an);
var esr = parseFloat(esr);

var diag;
if (bowel == 2 && (pyr + hr + an + esr) > 0) {
  diag = 'Severe';
} else if ((bowel + blood + pyr + hr + an + esr) == 0) {
  diag = 'Mild';
} else {
  diag = 'Moderate';
}

//results
calc_output.push({
    name: 'mini',
    value: diag,
    value_text: '',
    message: 'Severity of ulcerative colitis'
});
calc_output.push({
    name: 'Score',
    value: diag,
    value_text: '',
    message: 'Severity of ulcerative colitis'   
});
