calc.equation_logic_text:
var calc_output = [];

var a = parseFloat(a);
var l = parseFloat(l);
var b = parseFloat(b);
var e = parseFloat(e);
var s = parseFloat(s);
var type = parseFloat(type);

var A, L, B, E, S;

A = 'A' + a;

if (l <= 4) {
  L = 'L' + l;
} else if (l == 5) {
  L = '(L1 + L4)';
} else if (l == 6) {
  L = '(L2 + L4)';
} else if (l == 7) {
  L = '(L3 + L4)';
}

if (b <= 3) {
  B = 'B' + b;
} else if (b == 4) {
  B = 'B1p';
} else if (b == 5) {
  B = 'B2p';
} else if (b == 6) {
  B = 'B3p';
}

E = 'E' + e;

S = 'S' + s;

if (type == 0) {
  var crohn = A + ' ' + L + ' ' + B;
  var msg = "Montreal Classification for Crohn's disease";
} else {
  var crohn = E + ' ' + S;
  var msg = "Montreal Classification for UC";
}

//results
calc_output.push({
    name: 'mini',
    value: crohn,
    value_text: '',
    message: msg
});
calc_output.push({
    name: 'Score',
    value: crohn,
    value_text: '',
    message: msg   
});
