calc.equation_logic_text:
var calc_output = [];

var wbc = parseFloat(wbc);
var age = parseFloat(age);
var bili = parseFloat(bili);
var cr = parseFloat(cr);
var grade = parseFloat(grade);
var inr = parseFloat(inr);
var map = parseFloat(map);
var resp = parseFloat(resp);
var resp1 = parseFloat(resp1) || 0;
var resp2 = parseFloat(resp2) || 0;

if (resp == 0) {
  var organ_score = bili + cr + grade + inr + map + resp1;
} else if (resp == 1) {
  var organ_score = bili + cr + grade + inr + map + resp2;
}

var score = 10*(0.33*organ_score + 0.04*age + 0.63*Math.log(wbc) - 2);

var y_1 = -0.0022*Math.exp(0.0995*score);
var prob_1 = 1 - Math.pow(Math.E,y_1);
prob_1 = prob_1*100;
prob_1 = prob_1.toFixed(1);

var y_3 = -0.0079*Math.exp(0.0869*score);
var prob_3 = 1 - Math.pow(Math.E,y_3);
prob_3 = prob_3*100;
prob_3 = prob_3.toFixed(1);

var y_6 = -0.0115*Math.exp(0.0824*score);
var prob_6 = 1 - Math.pow(Math.E,y_6);
prob_6 = prob_6*100;
prob_6 = prob_6.toFixed(1);

var y_12 = -0.0231*Math.exp(0.0731*score);
var prob_12 = 1 - Math.pow(Math.E,y_12);
prob_12 = prob_12*100;
prob_12 = prob_12.toFixed(1);

score = score.toFixed(0);

//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: prob_12 +'% probability of death at 1 year'
});
calc_output.push({
    name: 'score',
    value: score,
    value_text: 'points',
    message: 'CLIF-C ACLF Score'
});
calc_output.push({
    name: 'TNM RCC',
    value: '',
    value_text: '',
    message: prob_1 + '% probability of death at 1 month<br/>' + prob_3 + '% probability of death at 3 months<br/>' + prob_6 + '% probability of death at 6 months<br/>' + prob_12 + '% probability of death at 1 year'
});
