calc.equation_logic_text:
var calc_output = [];

var q1 = parseFloat(q1);
var q2 = parseFloat(q2);
var q3 = parseFloat(q3);
var q4 = parseFloat(q4);
var q5 = parseFloat(q5);

var arm1 = parseFloat(arm1);
var arm2 = parseFloat(arm2);
var arm3 = parseFloat(arm3);
var arm4 = parseFloat(arm4);
var arm5 = parseFloat(arm5);

var leg1 = parseFloat(leg1);
var leg2 = parseFloat(leg2);
var leg3 = parseFloat(leg3);
var leg4 = parseFloat(leg4);

var ags = 0;

if (q1 == 1 && arm1 == 0 && arm2 == 0 && arm3 == 0 && arm4 == 0 && arm5 == 0) {
    ags = 1;
} else if (q1 == 1 && (arm1 == 1 || arm2 ==1 || arm3 == 1 || arm4 == 1 || arm5 == 1) && (arm1 != 2 && arm2 != 2 && arm3 != 2 && arm4 != 2 && arm5 != 2)) {
    ags = 2;
} else if (q1 == 1 && (arm1 == 2 || arm2 ==2 || arm3 == 2 || arm4 == 2 || arm5 ==2)) {
    ags = 3;
}

if (q1 == 1 && arm1 == 2 && arm2 == 2 && arm3 == 2 && arm4 == 2 && arm5 == 2 && q2 == 1) {
    ags = 4;
} else if (q1 == 1 && arm1 == 2 && arm2 == 2 && arm3 == 2 && arm4 == 2 && arm5 == 2 && q2 == 0) {
    ags = 5;
}

var lgs = 0;

if (leg1 == 1 && leg2 == 1 && leg3 ==0) {
    lgs = 1;
} else if (leg3 == 1 && leg4 == 0){
    lgs = 2;
}

if (leg4 == 1) {
    lgs = 3;
} else if (leg4 == 2) {
    lgs = 4;
} else if (leg4 == 3 && q3 == 1) {
    lgs = 5;
} else if (leg4 == 3 && q3 == 0 && q4 == 1) {
    lgs = 6;
} else if (leg4 ==3 && q3 == 0 && q4 == 0) {
    lgs = 7;
}

var score = ags + lgs;

calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: 'Overall Neuropathy Limitations Scale (ONLS)'
});
calc_output.push({
    name: 'ONLS',
    value: score,
    value_text: 'points',
    message: 'Overall Neuropathy Limitations Scale (ONLS)<br/><br/>Arms grade score: ' +  ags + ' points<br/>Legs grade score: ' + lgs + ' points'
});
