var calc_output = [];

var score = parseFloat(sfratio) + parseFloat(jaundice) + parseFloat(hypo) + parseFloat(gcs) + parseFloat(cr);

var risk;

if (score >= 0 && score <= 7) {
  risk = '4';
} else if (score >= 8 && score <= 11) {
  risk = '31';
} else if (score > 11) {
  risk = '58';	
}

//results
calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: risk + '% 30-day mortality' 
});
calc_output.push({
    name: 'score',
    value: score,
    value_text: 'points',
    message: 'mSOFA Score' 
});
calc_output.push({
    name: 'risk',
    value: risk,
    value_text: '%',
    message: '30-day mortality' 
});
