calc.equation_logic_text:
var calc_output = [];
var pxray = parseFloat(xray);
var page = parseFloat(age);
var phemo = parseFloat(hemo);
var pdys = parseFloat(dys);
var puncon = parseFloat(uncon);
var pcomorbid = parseFloat(comorbid);
var pcancer = parseFloat(cancer);
var pratio = parseFloat(ratio);
var plactate = parseFloat(lactate);

if (UOMSYSTEM == false) {
  var pbili = parseFloat(bili);
}

if (UOMSYSTEM == true) {
  var pbili = parseFloat(bili) * 17.104;
}

var totalPoint = (pxray*27.14635231) + (page*0.613879004) + (phemo*33.62099644) + (pdys*14.0569395) + (puncon*34.46174377) + (pcomorbid*10.38256228) + (pcancer*31.22108541) + (pratio*1.25) + (plactate*0.053380783) + (pbili*3.060498221);

var rate = (1.2205*pxray) + (0.0276*page) + (1.5116*phemo) + (0.632*pdys) + (1.5494*puncon) + (0.4668*pcomorbid) + (1.4037*pcancer) + (0.0562*pratio) + (0.0024*plactate) + (0.1376*pbili);

var x = rate - 6.6127;

var risk = (Math.exp(x) / (1 + Math.exp(x)))*100;

var group;

if(risk < 1.67924) {
  group = "Low";}
if(risk >= 1.67924 & risk < 40.40535) {
  group = "Medium";}
if(risk>=40.40535) {
  group = "High";}

calc_output.push({
   name: 'mini',
   value: risk.toFixed(1),
   value_text: '%',
   message: 'Risk of critical illness'
});
calc_output.push({
   name: 'COVID-GRAM Score',
   value: totalPoint.toFixed(0),
   value_text: 'points',
   message: 'COVID-GRAM Risk Score'
});
calc_output.push({
   name: 'COVID-GRAM Score',
   value: risk.toFixed(1),
   value_text: '%',
   message: 'Risk of critical illness, defined by admission to the intensive care unit (ICU), invasive ventilation, or death'
});
calc_output.push({
   name: 'COVID-GRAM Score',
   value: group,
   value_text: 'risk',
   message: 'Risk group'
});
