calc.equation_logic_text:
var calc_output = [];
var calc_msg = '';
var mini_msg = '';
var yesno = '';

if (altitude==0) {
	var output_score = (parseInt(age) + parseInt(hr) + parseInt(lowo2) + parseInt(dvt_pe_history) + parseInt(trauma) + parseInt(hemoptysis) + parseInt(estrogen) + parseInt(leg_swelling));
} else if (altitude==1) {
	var output_score = (parseInt(age) + parseInt(hr) + parseInt(higho2) + parseInt(dvt_pe_history) + parseInt(trauma) + parseInt(hemoptysis) + parseInt(estrogen) + parseInt(leg_swelling));
}


if (output_score == 0) {
	yesno = 'Yes';
	calc_msg = 'No need for further workup.<br/><br/>If no criteria are positive, PERC-Altitude Rule criteria are satisfied.';
	mini_msg = 'PE can be ruled out.';
} else {
	yesno = 'No';
	calc_msg = 'If any criteria are positive, the PERC-Altitude Rule cannot be used to rule out PE in this patient.';
	mini_msg = 'PE cannot be ruled out.';
}
calc_output.push({
	name: 'mini',
	value: yesno,
	value_text: ' ',
	message: mini_msg
});
calc_output.push({
	name: 'PERC Rule',
	value: output_score,
	value_text: 'criteria',
	message: calc_msg
});
