calc.equation_logic_text:
var calc_output=[];

var criteria=parseFloat(vte)+parseFloat(thrombophilia)+parseFloat(paralysis)+parseFloat(cancer)+parseFloat(immobilized)+parseFloat(icu)+parseFloat(age);

var risk;

if (criteria==0) {
  risk = '0.4';
}
if (criteria==1) {
  risk = '0.6';
}
if (criteria==2) {
  risk = '1.0';
}
if (criteria==3) {
  risk = '1.7';
}
if (criteria==4) {
  risk = '2.9';
}
if (criteria>=5 & criteria<=10) {
  risk = '7.2';
}
if (criteria>10) {
  risk = '>7.2';
}

calc_output.push({
    name: 'mini',
    value: criteria,
    value_text:'points',
    message:'IMPROVE Score'
});
calc_output.push({
    name: 'IMPROVE Score',
    value: criteria,
    value_text:'points',
    message:'IMPROVE Score'
});
calc_output.push({
    name: 'VTE Risk',
    value: risk,
    value_text:'%',
    message:'3-month risk of VTE'
});
