var calc_output = [];

var pattern = parseFloat(pattern);
var bleeding = parseFloat(bleeding);
var ulcers = parseFloat(ulcers);

var score = pattern + bleeding + ulcers;

var severity;

if (score <= 1) {
    severity = 'Remission';
} else if (score <= 4) {
    severity = 'Mild';
} else if (score <= 6) {
    severity = 'Moderate';
} else {
    severity = 'Severe'
}

calc_output.push({
