var calc_output = [];

// Declare vars

var age = parseFloat(age);
var stay = parseFloat(stay);
var location = parseFloat(location);
var therapy = parseFloat(therapy);
var unplanned = parseFloat(unplanned);
var surgerysite = parseFloat(surgerysite); 
var surgerystatus = parseFloat(surgerystatus);
var acuteinf = parseFloat(acuteinf);
var gcs = parseFloat(gcs);
var bilirubin = parseFloat(bilirubin);
var temp = parseFloat(temp);
var creatinine = parseFloat(creatinine);
var hr = parseFloat(hr);
var leukocytes = parseFloat(leukocytes);
var hydrogen = parseFloat(hydrogen); 
var platelets = parseFloat(platelets);
var sbp = parseFloat(sbp);
var oxygen = parseFloat(oxygen);
var cancertherapy = parseFloat(cancertherapy);
var chronichf = parseFloat(chronichf);
var hemcancer = parseFloat(hemcancer);
var cirrhosis = parseFloat(cirrhosis);
var aids = parseFloat(aids);
var metacancer = parseFloat(metacancer);
var cardioneuro = parseFloat(cardioneuro);
var cardiohem = parseFloat(cardiohem);
var digestiveacute = parseFloat(digestiveacute);
var neurocoma = parseFloat(neurocoma);
var cardioseptic = parseFloat(cardioseptic);
var cardioshock = parseFloat(cardioshock);
var hepatic = parseFloat(hepatic); 
var neurofocal = parseFloat(neurofocal);
var digestivepan = parseFloat(digestivepan); 
var neuromass = parseFloat(neuromass);

var score = 16 + age + stay + location + therapy + unplanned + surgerystatus + acuteinf + gcs + bilirubin + temp + creatinine + hr + leukocytes + hydrogen + platelets + sbp + oxygen + cancertherapy + chronichf + hemcancer + cirrhosis + aids + metacancer + cardioneuro + cardiohem + digestiveacute + neurocoma + cardioseptic + cardioshock + hepatic + neurofocal + digestivepan + neuromass;

if (surgerystatus == 0 || surgerystatus == 6) {
  score = score + surgerysite;
}

var x = -32.6659 + Math.log(score + 20.5958) * 7.3068;
var mortality = Math.exp(x)/(1 + Math.exp(x)) * 100;

calc_output.push({
   name: 'mini',
   value: score,
   value_text: 'points',
   message: mortality.toFixed(1) + '% in-hospital mortality'
});
calc_output.push({
   name: 'score',
   value: score,
   value_text: 'points',
   message: 'SAPS III Score'
});
calc_output.push({
   name: 'SAPS risk',
   value: mortality.toFixed(1),
   value_text: '%',
   message: 'In-hospital mortality by SAPS III'
});
