var calc_output = [];

// Declare vars

var traumatype = parseFloat(traumatype);
var rts = parseFloat(rts);
var iss = parseFloat(iss);
var a = parseFloat(a);

var b;

if (traumatype == 0) {
  b = -1.2470 + 0.9544*rts + -0.0768*iss + -1.9052*a;
}  else {
  b = -0.6029 + 1.1430*rts + -0.1516*iss + -2.6676*a;
}

var prob_survival = 1/(1 + Math.exp(-b)) * 100;

calc_output.push({
  name: 'mini',
  value: prob_survival.toFixed(1),
  value_text: '%',
  message: 'Probability of survival'
});
