calc.equation_logic_text:
var calc_output = [];

var chemo = parseFloat(chemo);

var dbp = parseFloat(dbp);
var iadl = parseFloat(iadl);
var ldh = parseFloat(ldh);

var hem = chemo + dbp + iadl + ldh;

var ecog = parseFloat(ecog);
var mmhs = parseFloat(mmhs);
var mna = parseFloat(mna);

var nhem = chemo + ecog + mmhs + mna;

var score = hem + nhem - chemo;

var risk;

if (score <= 3) {
    risk = 'Low';
} else if (score <= 6) {
    risk = 'Low-intermediate';
} else if ( score <= 9) {
    risk = 'Intermediate-high';
} else {
    risk = 'High';
}

calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: risk + ' risk of severe toxicity'
});
calc_output.push({
    name: 'combined',
    value: score,
    value_text: 'points',
    message: 'Combined score<br/>Hematologic score: ' + hem + ' points<br/>Nonhematologic score: ' + nhem + ' points'
});
calc_output.push({
    name: 'risk',
    value: risk,
    value_text: 'risk',
    message: 'Risk of severe toxicity'
});
