calc.equation_logic_text:
var calc_output = [];

var meds = parseFloat(meds);
var edh = parseFloat(edh);
var ivh = parseFloat(ivh);
var displaced = parseFloat(displaced);
var sdh8 = parseFloat(sdh8);
var iph8 = parseFloat(iph8);
var sah_bi = parseFloat(sah_bi);

var etoh = parseFloat(etoh);
var nondisplaced = parseFloat(nondisplaced);
var sdh4 = parseFloat(sdh4);
var iph4 = parseFloat(iph4);
var sah1 = parseFloat(sah1);

var mbig;
var msg;

if (meds == 1 || edh == 1 || ivh == 1 || displaced == 1 || sdh8 == 1 || iph8 == 1 || sah_bi == 1) {
    mbig = 'mBIG 3';
    msg = 'Proceed with current standard care'
} else if (etoh == 1 || nondisplaced == 1 || sdh4 == 1 || iph4 == 1 || sah1 == 1) {
    mbig = 'mBIG 2';
    msg = 'Proceed with hospital admission to ward for 24-48 hrs<br/>No repeat head CT<br/>No neurosurgery consult<br/>Q2 neurological assessment<br/>Trauma team assessment at 3 hrs<br/>Attending assessment at 6-12 hrs<br/>GCS 15 for discharge'
} else {
    mbig = 'mBIG 1';
    msg = 'No hospital admission necessary<br/>Proceed with 6 hr ED observation<br/>No repeat head CT<br/>No neurosurgery consult<br/>Q2 neurological assessment<br/>Trauma team assessment at 3 hrs<br/>Attending assessment at 6 hrs<br/>GCS 15 for discharge'
}

calc_output.push({
    name: 'mini',
    value: mbig,
    value_text: '',
    message: msg
});
calc_output.push({
    name: 'mBIG',
    value: mbig,
    value_text: '',
    message: msg
});
