var calc_output = [];

var swelling = parseFloat(swelling);
var hard = parseFloat(hard);
var absent = parseFloat(absent);
var nausea = parseFloat(nausea);
var high = parseFloat(high);

var score = swelling + hard + absent + nausea + high;

var risk;
var rec;

if (score <=2) {
    risk = 'Low';
    rec = 'No recommendation for ultrasound';
} else if (score <=4) {
    risk = 'Intermediate';
    rec = 'Ultrasound indicated';
} else {
    risk = 'High';
    rec = 'Surgical exploration recommended';
}

calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: 'TWIST Score'
});
calc_output.push({
    name: 'TWIST',
    value: score,
    value_text: 'points',
    message: 'TWIST Score'
});
calc_output.push({
    name: 'Risk',
    value: risk,
    value_text: 'risk',
    message: rec
});
