calc.equation_logic_text:
var calc_output = [];

var emboli = parseFloat(emboli);
var meningitis = parseFloat(meningitis);
var device = parseFloat(device);
var pre = parseFloat(pre);
var iv = parseFloat(iv);
var bact = parseFloat(bact);
var vert = parseFloat(vert);
var hc = parseFloat(hc);
var shock = parseFloat(shock);
var c = parseFloat(c);

var score = emboli + meningitis + device + pre + iv + bact + vert + hc + shock + c;

var risk;
var msg;

if (score < 3) {
    risk = 'Low';
    msg = '';
} else {
    risk = 'High'
    msg = '<br/><br/>Physicians are strongly encouraged to perform urgent echocardiography.';
}

calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: 'VIRSTA Score'
});
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: 'VIRSTA Score'
});
calc_output.push({
    name: 'Risk',
    value: risk,
    value_text: 'risk',
    message: 'Risk of IE' + msg
});
