var calc_output = [];

var height = parseFloat(height);
var waist = parseFloat(waist);
var sex = parseFloat(sex);

var rfm = 64 - (20 * (height/waist)) + (12 * sex);

var c = 'Not obese';
if (sex == 0 && rfm >= 22.8) {
    c = 'Obese';
}
if (sex == 1 && rfm >= 33.9) {
    c = 'Obese';
}

calc_output.push({
    name: 'mini',
    value: rfm.toFixed(1),
    value_text: '%',
    message: 'Relative Fat Mass'
});
calc_output.push({
    name: 'RFM',
    value: rfm.toFixed(1),
    value_text: '%',
    message: 'Relative Fat Mass'
});
calc_output.push({
    name: 'Class',
    value: c,
    value_text: '',
    message: 'Classification'
});
