calc.equation_logic_text:
var calc_output = [];

var ra = parseFloat(ra);
var hem = parseFloat(hem);
var iv = parseFloat(iv);
var iclass = parseFloat(iclass);
var rv = parseFloat(rv);

var score = ra + hem + iv + iclass + rv;

var risk;

if (score <= 2) {
    risk = 'Low';
} else if (score <= 4) {
    risk = 'Intermediate';
} else {
    risk = 'High';
}

calc_output.push({
    name: 'mini',
    value: score,
    value_text: 'points',
    message: risk + ' risk of RHF'
});
calc_output.push({
    name: 'Score',
    value: score,
    value_text: 'points',
    message: 'EUROMACS-RHF Score'
});
calc_output.push({
    name: 'Risk',
    value: risk,
    value_text: 'risk',
    message: 'Risk of RHF'
});
