calc.equation_logic_text:
calc_output = [];

var oud = parseFloat(oud);
var treatment = parseFloat(treatment);
var withdrawal = parseFloat(withdrawal);
var pregnant = parseFloat(pregnant);
var waiver = parseFloat(waiver);

var resultNumber = 0;

if (oud == 0) {
    resultNumber = 1;
} else if (treatment == 1) {
    resultNumber = 3;
} else if (withdrawal == 0) {
    if (pregnant == 1) {
        if (waiver == 1) {
            resultNumber = 11;
        } else if (waiver == 0) {
            resultNumber = 5;
        }
    } else if (pregnant == 0) {
        if (waiver == 1) {
            resultNumber = 10;
        } else if (waiver == 0) {
            resultNumber = 4;
        }
    }
} else if (withdrawal == 1) {
    if (pregnant == 1) {
        if (waiver == 1) {
            resultNumber = 13;
        } else if (waiver == 0) {
            resultNumber = 7;
        }
    } else if (pregnant == 0) {
        if (waiver == 1) {
            resultNumber = 12;
        } else if (waiver == 0) {
            resultNumber = 6;
        }
    } 
} else if (withdrawal == 2) {
    if (pregnant == 1) {
        if (waiver == 1) {
            resultNumber = 15;
        } else if (waiver == 0) {
            resultNumber = 9;
        }
    } else if (pregnant == 0) {
        if (waiver == 1) {
            resultNumber = 14;
        } else if (waiver == 0) {
            resultNumber = 8;
        }
    }
}

var header = '';
var msg = '';
var ns = '';

if (resultNumber == 1) {
    header = 'Advice: Does not meet criteria';
