calc.equation_logic_text:
var calc_output=[];
var mini_msg=''; 
var msgadd='';
var msg='';

if(parseFloat(temperature)+parseFloat(heart_rate)+parseFloat(respiratory_rate)+parseFloat(white_blood_cell_count)>=2){
  mini_msg='Meets SIRS criteria'; 
  msg='This patient meets SIRS criteria.';
  if(parseFloat(infection)==1){
    mini_msg='Meets sepsis criteria'; 
    msg='This patient meets sepsis criteria.';
    msgadd=' Follow your guidelines for sepsis, which typically include aggressive fluid resuscitation, early, broad-spectrum antibiotics, ICU consultation, CVP evaluation, and occasionally pressors and transfusion.';}
  if(parseFloat(lactic_acidosis)==1){
    mini_msg='Severe sepsis'; 
    msg='This patient meets severe sepsis criteria.';
  }
  if(parseFloat(severe_sepsis)==1){
    mini_msg='Septic shock'; 
    msg='This patient meets septic shock criteria.';
  }if(parseFloat(organ_failure)==1){
    mini_msg='Multi-organ dysfunction'; 
    msg='This patient meets multiple organ dysfunction syndrome.';
}}
else{
  mini_msg='Does not meet SIRS'; 
  msg='This patient does not meet SIRS criteria. For other causes of shock, see the Next Steps section.';
}

msg+=msgadd; 

calc_output.push({
  name:'mini',
  value:' ',
  value_text:' ',
  message:mini_msg
});
calc_output.push({
  name:'1096_SIRS & Sepsis Criteria_result',
  value:' ',
  value_text:' ',
  message:msg
});
