calc.equation_logic_text:
var calc_output = [];
var mini_msg = '';
var msg = '';
var u = parseInt(age_80) + parseInt(cancer) + parseInt(cardiopulmonary_disease) + parseInt(heart_rate_110) + parseInt(systolic_blood_pressure_100) + parseInt(o2_saturation_90);
if (u == 0) { mini_msg = 'Low';
    msg = '1.1% risk of death in the \u201cLow\u201d risk group (0 points), with 1.5% having recurrent thromboembolism or non-fatal bleeding';}
if (u > 0) { mini_msg = 'High';
    msg = '8.9% risk of death in the \u201cHigh\u201d risk group (>0 points)';}
calc_output.push({ name: 'mini', value: mini_msg, value_text: 'risk', message: ' ' });
calc_output.push({ name: 'Simplified PESI', value: mini_msg, value_text: 'risk', message: msg });
