var calc_output = [];
var msg = '';
var mini_msg = '';
var u = parseFloat(bun_25) + parseFloat(mental_status) + parseFloat(sirs_criteria) + parseFloat(age_60) + parseFloat(pleural_effusion);
if (u == 0) { msg = 'Patients with a BISAP Score of 0 had <1% risk of mortality, and one study stratified patients with a score \u22642, given a mortality risk of 1.9%.';
    mini_msg = '<1% risk of mortality.'; }
if (u > 0) { msg = 'Patients with a BISAP Score >0 had an increasing risk of mortality, with mortality increasing significantly with a score of 3 or greater. A score of 5 had a mortality rate of 22%.';
    mini_msg = 'Increasing risk of mortality.'; } 

calc_output.push({ 
    name: 'mini', 
    value: u.toFixed(0), 
    value_text: 'points', 
    message: mini_msg 
});
calc_output.push({ 
    name: '1735_BISAP Score_result', 
    value: u.toFixed(0), 
    value_text: 'points', 
    message: msg });
