calc.equation_logic_text:
var calc_output = [];
var u = 0;
var v;
var sbp = parseFloat(systolic_bp);
var creatinine = parseFloat(creatinine);
if (UOMSYSTEM == false) {
    creatinine = creatinine / 88.4;
}
if (parseFloat(systolic_bp) > 160) {
    sbp = 160;
}
hr = parseFloat(heart_rate);
if (parseFloat(heart_rate) < 80) {
    hr = 80;
}
if (parseFloat(heart_rate) > 120) {
    hr = 120;
}
sat = parseFloat(o2_sat);
if (parseFloat(o2_sat) > 92) {
    sat = 92;
}
u = parseFloat(age) * 2 + parseFloat(ems) + sbp * -1 + hr + sat * -2 + creatinine * 20 + parseFloat(potassium) + parseFloat(troponin) + parseFloat(cancer) + parseFloat(metolazone) + 12;
if (u <= -15.9) {
    msg = 'Lowest Risk of 7-day mortality.';
    v = '0.3%'
}
if (u >= -15.8 && u <= 17.9) {
    msg = 'Low Risk of 7-day mortality.';
    v = '0.7%'
}
if (u >= 18 && u <= 56.5) {
    msg = 'Intermediate Risk of 7-day mortality.';
    v = '2%'
}
if (u >= 56.6 && u <= 89.3) {
    msg = 'High Risk of 7-day mortality.';
    v = '3.5%'
}
if (u >= 89.4) {
    msg = 'Highest Risk of 7-day mortality. Consider admission.';
    v = '8.2%'
}
calc_output.push({ name: 'mini', value: u.toFixed(1), value_text: 'points', message: msg });
calc_output.push({ name: 'Risk of Mortality', value: v, value_text: ' ', message: msg });
calc_output.push({ name: 'EHMRG Score', value: u.toFixed(1), value_text: 'points', message: 'EHMRG Score' });
